import React,{useRef, useEffect} from "react";
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link } from "react-router-dom";

import logo from "../../assets/images/logo.png";
import './header.css';

const nav__links = [
    {
        path: "/home",
        display: "Home"
    },
    {
        path: "/about",
        display: "About"
    },
    {
        path: "/tours",
        display: "Tours"
    },
];

const Header = () => {

    const headerRef = useRef(null)

    const stickyHeaderFunc = ()=> {
        window.addEventListener('scroll', ()=>{
            if(document.body.scrollTop >80 || document.documentElement.scrollTop >80){
                headerRef.current.classList.add('sticky__header')
            }else{
                headerRef.current.classList.remove('sticky__header')
            }
        })
    }

    useEffect(()=>{
        stickyHeaderFunc()

        return window.removeEventListener('scroll', stickyHeaderFunc)
    })

    return <header className="header">
        <Container>
            <Row>
                <div className="nav__wrapper d-flex justify-content-between align-items-center">
                    {/* ====== logo ====== */}
                    <div className="logo">
                        <img src={logo} alt="" />
                    </div>
                    {/* ====== logo end====== */}

                    {/* ====== menu start====== */}
                    <div className="navigation">
                        <ul className="menu d-flex align-items-center gap-5">
                            {
                                nav__links.map((items, index) => (
                                    <li className="nav__item" key={index}>
                                        <NavLink to={items.path}
                                            className={
                                                navClass =>
                                                    navClass.isActive ? "active__link" : ""}>{items.display}
                                        </NavLink>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    {/* ====== menu end====== */}

                    <div className="nav__right d-flex align-items-center gap-4">
                        <div className="nav__btns d-flex align-items-center gap-4">
                            <Button className="btn seconday__btn">
                                <Link to='/login'>Login</Link>
                            </Button>
                            <Button className="btn primary__btn">
                                <Link to='/register'>Register</Link>
                            </Button>
                        </div>
                        <span className="mobile__menu">
                            <i class="ri-menu-line"></i>
                        </span>
                    </div>
                </div>
            </Row>
        </Container>
    </header>;
};

export default Header;
